import React, { useEffect, useState, useCallback } from "react";
import { useChannelStateContext, useChatContext } from "stream-chat-react";
import Board from "./Board";
import GameOver from "./GameOver";
import GameState from "./GameState";
import Reset from "./Reset";
import Mines from "./Mines";

function TicTacToe() {
  const { channel } = useChannelStateContext();
  const { client } = useChatContext();

  const [player, setPlayer] = useState(null);
  const [turn, setTurn] = useState("X");
  const [mistakeCounts, setMistakeCounts] = useState({ X: 0, O: 0 });
  const [tiles, setTiles] = useState(Array(64).fill(null));
  const [mines, setMines] = useState(Array(64).fill(null));
  const [mineCount, setMineCount] = useState(5);
  const [strike, setStrike] = useState(null);
  const [gameState, setGameState] = useState(GameState.mineCreation);

  useEffect(() => {
    const members = Object.keys(channel.state.members).sort();
    const myId = client.userID;
    const isFirst = members.indexOf(myId) === 0;
    setPlayer(isFirst ? "X" : "O");
  }, [channel, client.userID]);

  useEffect(() => {
    if (gameState === GameState.mineCreation) {
      const newGrid = Mines(mineCount);
      setMines(newGrid);
      setGameState(GameState.start);
    }
  }, [gameState, mineCount]);

  const handleTileClick = async (index) => {
    if (gameState !== GameState.start && gameState !== GameState.inProgress) return;
    if (turn !== player) return;
    if (tiles[index] !== null) return;

    const newTiles = [...tiles];
    newTiles[index] = mines[index];
    const newMineCount = mines[index] === -1 ? mineCount - 1 : mineCount;

    if (mines[index] === -1) {
      setMistakeCounts((prev) => ({
        ...prev,
        [player]: prev[player] + 1,
      }));
    }

    const newTurn = turn === "X" ? "O" : "X";
    const newGameState = newMineCount === 0 ? GameState.gameover : GameState.inProgress;

    await channel.sendEvent({
      type: "game-move",
      data: {
        tiles: newTiles,
        mines,
        mineCount: newMineCount,
        turn: newTurn,
        gameState: newGameState,
        mistakeCounts,
      },
    });
  };

  const handleReset = () => {
    setTiles(Array(64).fill(null));
    setTurn("X");
    setGameState(GameState.mineCreation);
    setStrike(null);
    setMineCount(5);
    setMistakeCounts({ X: 0, O: 0 });
  };

  useEffect(() => {
    const handler = (event) => {
      if (event.type === "game-move" && event.user.id !== client.userID) {
        setTiles(event.data.tiles);
        setMines(event.data.mines);
        setMineCount(event.data.mineCount);
        setTurn(event.data.turn);
        setGameState(event.data.gameState);
        setMistakeCounts(event.data.mistakeCounts);
      }
    };

    channel.on("event", handler);
    return () => channel.off("event", handler);
  }, [channel, client.userID]);

  useEffect(() => {
    const isBoardFull = !tiles.includes(null);
    if (isBoardFull) setGameState(GameState.tie);
  }, [tiles]);

  return (
    <div>
      <h1>Tic Tac Toe</h1>
      <h4>
        {turn}'s Turn | Mistakes - X: {mistakeCounts.X}, O: {mistakeCounts.O}
      </h4>
      <Board player={player} tiles={tiles} onTileClick={handleTileClick} strike={strike} />
      <GameOver gameState={gameState} />
      <Reset gameState={gameState} onReset={handleReset} />
    </div>
  );
}

export default TicTacToe;
